class MainActivity : ComponentActivity() {

    fun sendPrintEncodedImage(encodedStr:String){
        val bytes=Base64.getDecoder().decode(encodedStr);
        val bitmap=BitmapFactory.decodeByteArray(bytes,0,bytes.size);
        PrinterService.fromBitMap(bitmap);
    }

    val pickMedia = registerForActivityResult(ActivityResultContracts.PickVisualMedia()) { uri ->
        // Callback is invoked after the user selects a media item or closes the
        // photo picker.
        if (uri == null) {
            return@registerForActivityResult
        }
        Log.d("PhotoPicker", "Selected URI: $uri")
        val resolver=applicationContext.contentResolver;
        try{
            val bytes = resolver.openInputStream(uri)?.readBytes();
            if(bytes==null) {
                Toast.makeText(this, "Cannot resolve selected image", Toast.LENGTH_SHORT).show();
                return@registerForActivityResult;
            }

            PrinterService.fromBitMap(BitmapFactory.decodeByteArray(bytes,0,bytes.size));
        }catch (ex:Exception){
            Toast.makeText(applicationContext,ex.message?:"Something went wrong",Toast.LENGTH_SHORT).show();
        }

    }
    @OptIn(ExperimentalMaterial3Api::class)
    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)
        setContent {
            S680_printerTheme {
                // A surface container using the 'background' color from the theme
                // A surface container using the 'background' color from the theme
                Surface(
                    modifier = Modifier.fillMaxSize(),
                    color = MaterialTheme.colorScheme.background
                ) {
                    var text by remember { mutableStateOf("") }
                    var qrText by remember { mutableStateOf("") }
                    var barcodeText by remember { mutableStateOf("") }
                    var lines by remember {
                        mutableStateOf(mutableListOf<String>())
                    }
                    fun pickImageToPrint(){
                        // Launch the photo picker and let the user choose only images.
                        pickMedia.launch(PickVisualMediaRequest(ActivityResultContracts.PickVisualMedia.ImageOnly))
                    }

                    fun sendImageSample(){
                        //Base64 encoded bitmap
                        sendPrintEncodedImage("");
                    }

                    fun sendPrint(){
                        PrinterService.printLines(lines);
                    }
                    Column(

                        Modifier.padding(vertical = 20.dp)
                            .verticalScroll(rememberScrollState())
                          ,

//                        verticalArrangement = Arrangement.SpaceEvenly ,
                        horizontalAlignment = Alignment.CenterHorizontally)
                    {

                        OutlinedButton(
                            modifier = Modifier.padding(vertical = 10.dp),
                            onClick = {
                                pickImageToPrint();
                            }){
                            Row {
                                Icon(Icons.Outlined.ExitToApp ,null)
                                Box(modifier = Modifier.size(5.dp,1.dp))
                                Text("Pick Image")
                            }
                        }


                        ElevatedButton(
                            modifier = Modifier.padding(vertical = 40.dp),
                            onClick = {
                                sendImageSample();
                            }){
                            Row {
                                Icon(Icons.Outlined.PlayArrow ,null)
                                Box(modifier = Modifier.size(5.dp,1.dp))
                                Text("Send image sample print request")
                            }
                        }
                        Text("Add Text, line by line")
                        TextField(
                            label = { Text("Line") },
                            value = text,
                            maxLines = 2,
                            trailingIcon = {
                                IconButton(
                                    onClick = {
                                        lines.add(text);
                                        text="";
                                    },
                                ) {
                                    Icon(
                                        Icons.Default.Add,
                                        contentDescription = ""
                                    )
                                }
                            },
                            onValueChange = {
                                text = it;
                            },
                        )
                        ElevatedButton(
                            enabled = lines.isNotEmpty(),
                            onClick = {sendPrint();
                                lines= mutableListOf();

                            },){
                            Row {
                                Icon(Icons.Default.Add,null)
                                Box(modifier = Modifier.size(5.dp,1.dp))
                                Text("Send Print Request")
                            }
                        }
                        Column (

                            Modifier
                                .wrapContentSize()
                                .padding(all = 8.dp),
                            horizontalAlignment = Alignment.Start
                        ){
                            for (i in 0 until lines.size)
                                Text("${i+1}. ${lines[i]}")
                        }

                        Box(modifier = Modifier.size(5.dp,15.dp))
                        Column (

                            Modifier
                                .wrapContentSize()
                                .padding(all = 8.dp),
                            horizontalAlignment = Alignment.Start
                        ){
                            Text("text for qr code")
                            TextField(
                                placeholder= { Text("www.example.com") },
                                value = qrText,
                                maxLines = 2,
                                trailingIcon = {
                                    IconButton(
                                        enabled = qrText.isNotEmpty(),
                                        onClick = {
                                            PrinterService.printQrCode(qrText);
                                            qrText="";
                                        },
                                    ) {
                                        Icon(
                                            Icons.Default.Send,
                                            contentDescription = ""
                                        )
                                    }
                                },
                                onValueChange = {
                                    qrText = it;
                                },
                            )
                        }
                        Box(modifier = Modifier.size(5.dp,15.dp))
                        Column (

                            Modifier
                                .wrapContentSize()
                                .padding(all = 8.dp),
                            horizontalAlignment = Alignment.Start
                        ){
                            Text("Enter product code")
                            TextField(
                            placeholder = { Text("123456789112") },
                                value = barcodeText,
                                maxLines = 2,
                                trailingIcon = {
                                    IconButton(
                                        enabled = barcodeText.isNotEmpty(),
                                        onClick = {
                                            PrinterService.printBarcode(barcodeText);
                                            barcodeText="";
                                        },
                                    ) {
                                        Icon(
                                            Icons.Default.ShoppingCart,
                                            contentDescription = ""
                                        )
                                    }
                                },
                                onValueChange = {
                                    barcodeText = it;
                                },
                            )
                        }
                    }
                }
            }
        }
    }
}

@Composable
fun Greeting(name: String, modifier: Modifier = Modifier) {
    Text(
        text = "Hello $name!",
        modifier = modifier
    )
}

